const HOCPart2 = WrappedComponent =>{
    class HOCPart2 extends React.Component{

        constructor(props) {
            super(props)
          
            this.state = {
               count: 0
            }
          }
          
          incrementCounter = () =>{
            this.setState(prevState=>{    //We need the previous state increment the value the argument to setState will not be a simple object
                return {count: prevState.count + 1}
            })       
          }
        render(){ 
            return <WrappedComponent count={this.state.count} incrementCounter={this.incrementCounter}/>
        }
    }
