import React, { Component } from 'react'
import UpdatedComponent from './HOCPart2'

class HigherOrderPart1_Hover extends Component {
    constructor(props) {
      super(props)
    
      this.state = {
         count: 0
      }
    }

    incrementCounter = () =>{
        this.setState(prevState=>{    //We need the previous state increment the value the argument to setState will not be a simple object
            return {count: prevState.count + 1}
        })       
      }
    
  render() {
        const {count} = this.state
        return <h2 onMouseOver={this.incrementCounter}>{}Hovered {count} times</h2>
  }
}

export default UpdatedComponent (HigherOrderPart1_Hover)
