import React, { Component } from 'react'
import UpdatedComponent from './HOCPart2'

class HigherOrderPart1 extends Component {
  constructor(props) {
    super(props)
  
    this.state = {
       count: 0
    }
  }
  
  incrementCounter = () =>{
    this.setState(prevState=>{    //We need the previous state increment the value the argument to setState will not be a simple object
        return {count: prevState.count + 1}
    })       
  }
  render() {
    const {count} = this.state
    return (
      <div>
        <button onClick={this.incrementCounter}>Clicked {count} times</button>
      </div>
    )
  }
}

export default UpdatedComponentHigherOrderPart1
