import React, { Component } from 'react'
class HigherOrderPart1_Keypress extends Component {
    constructor(props) {
      super(props)
    
      this.state = {
         count: 0
      }
    }
    
    incrementCounter = () =>{
        this.setState(prevState=>{    //We need the previous state increment the value the argument to setState will not be a simple object
            return {count: prevState.count + 1}
        })       
      }
  render() {
    const {count} = this.state
     return <h2 onKeyUp={this.incrementCounter}>Keys Up {count} times</h2>
  }
}

export default HigherOrderPart1_Keypress
