    const handleClick = () =>{
        // persons.push('Clark')
        // persons.push('Panchal')
        // setPersons(persons)

        const newPerson = {...persons}
        newPerson.push('Piya')
    }
    console.log('ArrayState Render')
  return (
    <div>
       <button onClick={handleClick}>Click</button>
       {
            persons.map(person => (<div key={person}>{person}</div>))
       }
    </div>
  )
}

export default ArrayUseState
