import React, {useEffect, useRef} from 'react'

function FocusInput() {
    const inputRef = useRef(null)  //Initially hold null value
    useEffect(() =>{
        //Focus the input element
        inputRef.current
    },[])
  return (
    <div>
      <input ref= {inputRef} type='text'/>
    </div>
  )
}

export default FocusInput
